template.script:
function setCookie(name,value,days) {
var expires = "";
if (days) {
var date = new Date();
date.setTime(date.getTime() + (days*24*60*60*1000));
expires = "; expires=" + date.toUTCString();
}
document.cookie = name + "=" + (value || "") + expires + "; path=/; domain=localhost:8023";
// "; path=" + path +
// "; domain=" + domain
}

console.log('Datos enviados desde el Controlador:');
var redirect_url = '';
console.log('redirect_url', redirect_url);

var cookies = '';
console.log('cookies', cookies);

var session_id = '';
setCookie('session_id',encodeURIComponent( session_id), 30);
