                function setCookie(name,value,days) {
                    var expires = "";
                    if (days) {
                        var date = new Date();
                        date.setTime(date.getTime() + (days*24*60*60*1000));
                        expires = "; expires=" + date.toUTCString();
                        }
                    document.cookie = name + "=" + (value || "") + expires + "; path=/; domain=9999.geztion.pro";
                    // "; path=" + path  +
                    // "; domain=" + domain
                }

